mapper.select:
select K.TASK_ID, K.TASK_TITLE, K.TASK_CONTENT, K.TASK_STAFF, to_char(K.TASK_DATE,'MM-DD') TASK_DATE, K.FINISH_DATE, K.STATE, K.STEP_ID, K.PARENT_TASK From PMS_PROJ_TASK K where K.PROJ_ID = #{projId}
and K.STEP_ID = #{stepId}
order by K.TASK_ID asc

mapper.insert:

	SELECT PMS_PROJ_TASK_SEQ.NEXTVAL FROM DUAL

insert into PMS_PROJ_TASK (TASK_ID, PROJ_ID, TASK_TITLE, TASK_STAFF, TASK_STAFF_ID, TASK_DATE, STATE, CTEATE_STAFF, STEP_ID, RATE, FINISH_DATE
, PARENT_TASK
)
values (#{taskId}, #{projId}, #{taskTitle}, #{taskStaff}, #{taskStaffId}, sysdate, '0', #{userId},  #{stepId}, '0%', to_date(#{finishDate}, 'yyyy-mm-dd')
, #{parentTask}
)

mapper.update:
update PMS_PROJ_STEP

	STATE = #{state} 
	FINISH_STAFF = #{userId} 

 WHERE STEP_ID = #{stepId}

mapper.select:
select count(1)
from PMS_PROJ_TASK T
where T.PARENT_TASK = #{taskId}

mapper.update:
UPDATE PMS_PROJ_TASK
TASK_TITLE = #{taskTitle} , TASK_STAFF = #{taskStaff} , TASK_STAFF_ID = #{taskStaffId} , TASK_CONTENT = #{taskContent} , STATE = #{state} , FINISH_DATE =to_date(#{finishDate},'YYYY-MM-DD')
WHERE TASK_ID = #{taskId}
