mapper.update:
UPDATE PMS_PROJ_TEMPLATE
SET IS_DEFAULT = '1'
WHERE TEMP_ID = #{tempId}

UPDATE PMS_PROJ_TEMPLATE

    TEMP_NAME = #{tempName} ,
    DESCRIPTION = #{description}

WHERE TEMP_ID = #{tempId}

UPDATE PMS_PROJ_TEMPLET_STEP
STEP_NAME = #{stepName} , STEP_SORT = #{stepSort} , DEFAULT_MOVE_DATE = #{defaultMoveDate} , FINISH_NOTICE_DATE = #{finishNoticeDate} , NOTICE_STAFF = #{noticeStaff} , NOTICE_STAFF_ID = #{noticeStaffId} , TASK_CHANGE_STAFF = #{taskChangeStaff} , TASK_CHANGE_STAFF_ID = #{taskChangeStaffId} , FINISH_SCHEDULE_NOTICE_DATE = #{finishScheduleNoticeDate} , FINISH_SCHEDULE_STAFF = #{finishScheduleStaff} , IS_ATTACH = #{isAttach} , ATTACH_WORD = #{attachWord} , ATTACH_EXCEL = #{attachExcel} , ATTACH_PDF = #{attachPdf} , ATTACH_CONTENT = #{attachContent}
WHERE TEMP_STEP_ID = #{tempStepId}

UPDATE PMS_PROJ_TEMPLATE
SET STATE = '0'
WHERE TEMP_ID = #{tempId}

mapper.select:
SELECT PROJ_ID
FROM PMS_PROJECT_INFO
WHERE TEMP_ID = #{tempId}
